#import "Base.h"#import "Platform_Ios.h"#import "AppController.h"#import "RootViewController.h"// 打开一个网页， 会退出游戏，进入到浏览器界面void toOpenNetworkURL(const char* _urlString){    NSString *urlStr = [[NSString alloc] initWithUTF8String: _urlString];        NSURL* url = [NSURL URLWithString:urlStr];    [[UIApplication sharedApplication] openURL:url];        [urlStr release];}bool gAdCanShow = true;   // 默认显示广告void setAdViewShow(bool _isShow){    gAdCanShow = _isShow;        if(gSysCtl.gameViewConTronller)    {        if(gAdCanShow)        {            [gSysCtl.gameViewConTronller setAdViewHidden:NO];        }        else        {            [gSysCtl.gameViewConTronller setAdViewHidden:YES];        }    }}